// Declaração das variáveis para as três notas
var nota1 = 6.5;
var nota2 = 5.0;
var nota3 = 8.5;

// Cálculo da média das três notas
var media = (nota1 + nota2 + nota3) / 3;

// Exibe o resultado da média no console
console.log("A média das notas é: " + media.toFixed(2)); // .toFixed(2) para exibir a média com duas casas decimais
